SELECT @{REPLACE(activity('RemoveBlankSpacesFromColumnNamesDelta').output.firstRow.COLUMNS, '&quot;', '"')}, ct.SYS_CHANGE_VERSION as SysChangeVersion, ct.SYS_CHANGE_OPERATION as SysChangeOperation FROM @{item().SourceSchemaName}.[@{item().SourceTableName}] ds
RIGHT OUTER JOIN CHANGETABLE(CHANGES @{item().SourceSchemaName}.[@{item().SourceTableName}], @{item().ChangeTrackingVersion}) as ct on @{activity('SplitSourceKeyColumns').output.firstRow.SplitSourceKeyColumns} 
where ct.SYS_CHANGE_VERSION <= @{activity('LookupCurrentChangeTrackingVersion').output.firstRow.CurrentChangeTrackingVersion}
